import React, { useState } from "react";
import { motion } from "framer-motion";

function Services() {
  const [bgImage, setBgImage] = useState("/voiture.web");

  const allServices = [
    {
      title: "Affordable Luxury",
      description: "Enjoy the perfect blend of comfort and affordability with our premium services, offering a luxurious travel experience within your budget."
    },
    {
      title: "Exceptional Service",
      description: "Experience professionalism and dedication with our customer-centric approach, ensuring every journey exceeds your expectations."
    },
    {
      title: "Tailored Solutions",
      description: "We customize travel plans to suit your unique preferences and needs, ensuring a personalized and seamless experience."
    },
    {
      title: "Expert Knowledge",
      description: "Explore Morocco's hidden gems and cultural treasures guided by our experienced team, ensuring authentic and enriching experiences."
    }
  ];

  console.log("All Services:", allServices); // Vérification des données

  return (
    <div className="bg-white py-12 min-h-screen flex flex-col items-center px-6">
      <h2 className="text-4xl font-bold text-blue-600 mb-10 italic">
        Why Choose Our Services?
      </h2>

      <div
        id="home"
        className="bg-cover bg-center h-screen flex flex-col items-center justify-center text-center transition-all duration-1000 ease-in-out mt-20 pt-6 italic"
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundPosition: "center top"
        }}
      >
        {/* Vérification si des services sont disponibles */}
        {allServices.length === 0 ? (
          <p className="text-red-500 text-xl">No services available</p>
        ) : (
          <div className="flex gap-6 justify-center w-full flex-wrap mt-[-50px]">
            {allServices.map((pkg, index) => (
              <motion.div
                key={index}
                className="relative bg-white shadow-md rounded-lg overflow-hidden w-80 p-6"
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                <h3 className="text-xl font-bold text-blue-600 mb-2">
                  {pkg.title}
                </h3>
                <p className="text-gray-700">{pkg.description}</p>
              </motion.div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default Services;
