import React from "react";
import { useLocation } from "react-router-dom";
import { FaHiking, FaCity, FaLandmark, FaSpa, FaUmbrellaBeach, FaGift, FaCampground, FaUsers } from "react-icons/fa";

// صور لكل نوع من الأنشطة
const activityImages = {
  "outdoor": "/images/outdoor.jpg",
  "city-tours": "/images/city-tours.jpg",
  "cultural": "/images/cultural.jpg",
  "luxury": "/images/luxury.jpg",
  "family": "/images/family.jpg",
  "relaxation": "/images/relaxation.jpg",
  "holiday": "/images/holiday.jpg",
  "adventure": "/images/adventure.jpg",
};

function ActivitiesPage() {
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const type = params.get("type");

  const getActivityIcon = (type) => {
    switch (type) {
      case "outdoor":
        return <FaHiking className="text-blue-500" />;
      case "city-tours":
        return <FaCity className="text-blue-500" />;
      case "cultural":
        return <FaLandmark className="text-blue-500" />;
      case "luxury":
        return <FaSpa className="text-blue-500" />;
      case "family":
        return <FaUsers className="text-blue-500" />;
      case "relaxation":
        return <FaUmbrellaBeach className="text-blue-500" />;
      case "holiday":
        return <FaGift className="text-blue-500" />;
      case "adventure":
        return <FaCampground className="text-blue-500" />;
      default:
        return <FaCity className="text-blue-500" />;
    }
  };

  return (
    <div className="p-10">
      <h1 className="text-3xl font-bold text-blue-600 mb-4">
        Activity: {type ? type.replace("-", " ") : "All Activities"}
      </h1>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        <div className="bg-white p-6 rounded-lg shadow-md">
          <img src={activityImages[type] || "/images/default.jpg"} alt={type} className="w-full h-48 object-cover rounded-lg mb-4" />
          <div className="flex items-center mb-2">
            {getActivityIcon(type)}
            <h3 className="text-xl font-semibold text-gray-700 ml-2">{type ? type.replace("-", " ") : "General Activities"}</h3>
          </div>
          <p className="text-gray-700">
            Explore our collection of {type ? type.replace("-", " ") : "various"} tours. Discover the beauty and excitement of different experiences.
          </p>
        </div>
      </div>
    </div>
  );
}

export default ActivitiesPage;
