import React, { useState } from "react";
import { motion } from "framer-motion";

function Services() {
  const [bgImage, setBgImage] = useState("/casa.jpg");

  const allServices = [
    { title: "Affordable Luxury", description: "Enjoy the perfect blend of comfort and affordability with our premium services, offering a luxurious travel experience within your budget." },
    { title: "Exceptional Service", description: "Experience professionalism and dedication with our customer-centric approach, ensuring every journey exceeds your expectations." },
    { title: "Tailored Solutions", description: "We customize travel plans to suit your unique preferences and needs, ensuring a personalized and seamless experience." },
    { title: "Expert Knowledge", description: "Explore Morocco's hidden gems and cultural treasures guided by our experienced team, ensuring authentic and enriching experiences." }
  ];

  return (
    <div className="bg-white py-12 min-h-screen flex flex-col items-center px-6">
      <h2 className="text-4xl font-bold text-blue-600 mb-10 italic">
        Why Choose Our Services?
      </h2>

      <div
        id="home"
        className="bg-cover bg-center h-screen flex flex-col items-center justify-center text-center transition-all duration-1000 ease-in-out mt-28 pt-6 italic"
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundPosition: "center top"
        }}
      >
        <div className="w-full text-center text-lg text-gray-800 font-medium">
          {allServices.map((service, index) => (
            <span key={index}>
              {service.title} - {service.description}
              {index < allServices.length - 1 && " | "}
            </span>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Services;
