import React from "react";
import { Link } from "react-router-dom";

function ActivitiesSection() {
  const activities = [
    "Outdoor Activities",
    "City Tours",
    "Cultural & Thematic Tours",
    "Indulgence & Luxury Tours",
    "Family Friendly Tours",
    "Relaxation Tours",
    "Holiday & Seasonal Tours",
    "Wild & Adventure Tours",
  ];

  return (
    <div className="bg-white rounded-lg p-6 px-20 mb-10">
      <h2 className="text-3xl font-bold text-gray-800 mb-4 italic">
        Browse Tours By Activity
      </h2>
      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        {activities.map((activity, index) => {
          // نحولو activity ل query string (مثلا: Outdoor Activities => Outdoor-Activities)
          const queryType = activity.replace(/\s+/g, "-");

          return (
            <Link
              key={index}
              to={`/activities?type=${queryType}`}
              className="text-blue-600 hover:underline font-semibold text-lg"
            >
              {activity}
            </Link>
          );
        })}
      </div>
    </div>
  );
}

export default ActivitiesSection;
