import React, { useState } from 'react';

function Hero() {
  const images = ['/ouarzazate.jpg', '/casa.jpg', '/rabatt.jpg', '/tanger.jpg'];
  const cities = ['Ouarzazate', 'Casablanca', 'Rabat', 'Tanger'];
  const descriptions = [
    'Discover the charm of Ouarzazate, the gateway to the Sahara!',
    'Explore the lively city of Casablanca, a fusion of tradition and modernity.',
    'Visit Rabat, the beautiful capital of Morocco with a rich history.',
    'Tanger, where Europe meets Africa, offering a blend of cultures and landscapes.',
  ];

  const buttonTexts = [
    'Book your Sahara Adventure!',
    'Start your Casablanca Journey!',
    'Explore Rabat Now!',
    'Visit the Magic of Tanger!',
  ];

  const buttonColors = [
    'bg-[#6F4F37]',  // Ouarzazate Marron
    'bg-blue-500',    // Casablanca Blue
    'bg-sky-300',     // Rabat Sky Blue
    'bg-blue-700',    // Tanger Dark Blue
  ];

  const [currentIndex, setCurrentIndex] = useState(1); // Par défaut sur Casablanca

  const changeImage = () => {
    const nextIndex = (currentIndex + 1) % images.length;
    setCurrentIndex(nextIndex);
    console.log('New Image:', images[nextIndex]); // Debug
  };

  return (
    <div
      id="home"
      className="relative h-screen flex items-center justify-center text-center transition-all duration-1000 ease-in-out mt-28 pt-6 italic"
      style={{
        backgroundImage: `url(${images[currentIndex]})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center top',
      }}
    >
      {/* Contenu du Hero */}
      <div className="absolute top-2 left-2 bottom-0 p-12 flex flex-col justify-center items-center text-white w-1/3">
        <h1 className="text-6xl font-bold mb-6">{cities[currentIndex]}</h1>
        <p className="text-xl mb-8">{descriptions[currentIndex]}</p>
        <div className="mt-6">
          <button
            className={`${buttonColors[currentIndex]} py-4 px-10 rounded-lg hover:bg-opacity-80 transition`}
            onClick={changeImage}
          >
            {buttonTexts[currentIndex]}
          </button>
        </div>
      </div>
      
      {/* Flèche de navigation */}
      <div
        onClick={changeImage}
        className="absolute right-6 top-1/2 transform -translate-y-1/2 cursor-pointer text-white text-5xl"
      >
        &#8594;
      </div>
    </div>
  );
}

export default Hero;
