import React from "react";
import { Link } from "react-router-dom";

function Activities() {
  const activities = [
    { name: "Outdoor Activities", type: "outdoor" },
    { name: "City Tours", type: "city-tours" },
    { name: "Cultural & Thematic Tours", type: "cultural-tours" },
    { name: "Indulgence & Luxury Tours", type: "luxury-tours" },
    { name: "Family Friendly Tours", type: "family" },
    { name: "Relaxation Tours", type: "relaxation" },
    { name: "Holiday & Seasonal Tours", type: "holiday" },
    { name: "Wild & Adventure Tours", type: "adventure" },
  ];

  return (
    <div className="bg-white rounded-lg p-6 px-20 mb-10">
      <h2 className="text-3xl font-bold text-gray-800 mb-4 italic">Browse Tours By Activity</h2>
      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        {activities.map((activity) => (
          <Link
            key={activity.type}
            to={`/activities?type=${activity.type}`}
            className="text-blue-600 hover:underline font-semibold text-lg"
          >
            {activity.name}
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Activities;
