import React, { useState } from "react";
import { motion } from "framer-motion";

function Services() {
  const [bgImage, setBgImage] = useState("/casa.jpg");

  const allServices = [
    {
      title: "Affordable Luxury",
      description: "Enjoy the perfect blend of comfort and affordability with our premium services, offering a luxurious travel experience within your budget."
    },
    {
      title: "Exceptional Service",
      description: "Experience professionalism and dedication with our customer-centric approach, ensuring every journey exceeds your expectations."
    },
    {
      title: "Tailored Solutions",
      description: "We customize travel plans to suit your unique preferences and needs, ensuring a personalized and seamless experience."
    },
    {
      title: "Expert Knowledge",
      description: "Explore Morocco's hidden gems and cultural treasures guided by our experienced team, ensuring authentic and enriching experiences."
    }
  ];

  console.log("All Services:", allServices); // Vérification des données

  // État pour gérer la pagination
  const [currentPage, setCurrentPage] = useState(0);
  const itemsPerPage = 3;
  
  const displayedServices = allServices.slice(currentPage, currentPage + itemsPerPage);

  return (
    <div className="bg-white py-12 min-h-screen flex flex-col items-center px-6">
      <h2 className="text-4xl font-bold text-blue-600 mb-10 italic">
        Why Choose Our Services?
      </h2>

      <div
        id="home"
        className="bg-cover bg-center h-screen flex flex-col items-center justify-center text-center transition-all duration-1000 ease-in-out mt-28 pt-6 italic"
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundPosition: "center top"
        }}
      >
        {/* Vérification si des services sont disponibles */}
        {displayedServices.length === 0 ? (
          <p className="text-red-500 text-xl">No services available</p>
        ) : (
          <div className="flex gap-6 justify-center w-full">
            {displayedServices.map((pkg, index) => (
              <motion.div
                key={index}
                className="relative bg-white shadow-md rounded-lg overflow-hidden w-80 border border-red-500 p-6"
                initial={{ opacity: 0, x: 50 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.5 }}
              >
                <h3 className="text-xl font-bold text-blue-600 mb-2">
                  {pkg.title}
                </h3>
                <p className="text-gray-700">{pkg.description}</p>
              </motion.div>
            ))}
          </div>
        )}

        {/* Pagination */}
        <div className="mt-8 flex gap-4">
          <button
            className="px-4 py-2 bg-blue-500 text-white rounded-lg disabled:opacity-50"
            onClick={() => setCurrentPage((prev) => Math.max(prev - itemsPerPage, 0))}
            disabled={currentPage === 0}
          >
            Previous
          </button>
          <button
            className="px-4 py-2 bg-blue-500 text-white rounded-lg disabled:opacity-50"
            onClick={() => setCurrentPage((prev) => (prev + itemsPerPage < allServices.length ? prev + itemsPerPage : prev))}
            disabled={currentPage + itemsPerPage >= allServices.length}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
}

export default Services;
