import React from "react";
import { motion } from "framer-motion";

function Services() {
  const bgImage = "/voiture.webp";

  const allServices = [
    {
      title: "Affordable Luxury",
      description: "Enjoy the perfect blend of comfort and affordability with our premium services, offering a luxurious travel experience within your budget."
    },
    {
      title: "Exceptional Service",
      description: "Experience professionalism and dedication with our customer-centric approach, ensuring every journey exceeds your expectations."
    },
    {
      title: "Tailored Solutions",
      description: "We customize travel plans to suit your unique preferences and needs, ensuring a personalized and seamless experience."
    },
    {
      title: "Expert Knowledge",
      description: "Explore Morocco's hidden gems and cultural treasures guided by our experienced team, ensuring authentic and enriching experiences."
    }
  ];

  return (
    <div className="bg-white py-12 min-h-screen flex flex-col items-center px-6">
      <h2 className="text-4xl font-bold text-blue-600 mb-10 italic">
        Why Choose Our Services?
      </h2>

      <div
        id="home"
        className="bg-cover bg-center h-[70vh] flex flex-col items-center justify-center text-center transition-all duration-1000 ease-in-out mt-20 pt-6 italic"
        style={{
          backgroundImage: `url(${bgImage})`,
          backgroundPosition: "center top"
        }}
      >
        {/* Vérification si des services sont disponibles */}
        {allServices.length === 0 ? (
          <p className="text-red-500 text-xl">No services available</p>
        ) : (
          <div className="flex gap-6 justify-center w-full flex-wrap mt-[-50px]">
            {allServices.map((pkg, index) => (
              <motion.div
                key={index}
                className="relative bg-white rounded-lg overflow-hidden w-80 p-6"
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                <h3 className="text-xl font-bold text-blue-600 mb-2">
                  {pkg.title}
                </h3>
                <p className="text-gray-700">{pkg.description}</p>
              </motion.div>
            ))}
          </div>
        )}
      </div>

      {/* Section Popular Destinations */}
      <div className="mt-20 max-w-4xl text-center">
        <h2 className="text-3xl font-bold text-blue-600 mb-6 italic">
          Popular Destinations
        </h2>
        <p className="text-gray-700 text-lg leading-relaxed">
          Morocco’s most popular tourist destinations offer a rich blend of culture, history, and natural beauty. <br />
          <span className="font-semibold text-blue-500">Marrakech Safi</span> enchants visitors with its vibrant medina, souks, and landmarks like Jemaa el-Fnaa. <br />
          <span className="font-semibold text-blue-500">Fes</span> is known for its ancient walled city and the world’s oldest university. <br />
          <span className="font-semibold text-blue-500">Chefchaouen</span>, the “Blue City,” captivates with its striking blue-painted streets. <br />
          <span className="font-semibold text-blue-600">Sahara Desert</span> offers unforgettable desert adventures, while <span className="font-semibold text-blue-500">Essaouira</span> and <span className="font-semibold text-blue-500">Agadir</span> attract beach lovers. <br />
          And finally, <span className="font-semibold text-blue-600">Rabat</span> reflects Morocco’s diverse charm as its capital.
        </p>
      </div>
    </div>
  );
}

export default Services;
